import * as React from "react";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import Stack from "@mui/material/Stack";
import { useState, useEffect } from "react";

export default function Bowler({
  selectedPlayer,
  setSelectedPlayer,
  countPlayer,
  setCountPlayer,
  countCredit,
  setCountCredit,
  error,
  setError,
}) {
  const [player, setPlayer] = useState([]);
  const fetchData = () => {
    return fetch(
      " https://leaguex.s3.ap-south-1.amazonaws.com/task/fantasy-sports/Get_All_Players_of_match.json"
    )
      .then((response) => response.json())
      .then((data) =>
        setPlayer(data ? data.filter((info) => info?.role === "Bowler") : [])
      );
  };

  useEffect(() => {
    fetchData();
    console.log(
      "---->Bowler",
      player.filter((data) => data?.role === "Bowler")
    );
  }, []);

  useEffect(() => {
    if (
      selectedPlayer.filter((data) => data?.role === "Bowler").length >= 3 &&
      selectedPlayer.filter((data) => data?.role === "Bowler").length <= 7
    ) {
      setError(false);
    } else {
      setError(true);
    }
  }, [selectedPlayer]);

  console.log("playerdata", player);
  return (
    <Stack spacing={3} sx={{ width: 500 }}>
      <div>
        <p>Pick 3-7 Bowler</p>
        <Autocomplete
          multiple
          id="tags-outlined"
          options={player}
          value={selectedPlayer.filter((data) => data?.role === "Bowler")}
          getOptionLabel={(option) =>
            option.name + "\t(" + option.event_player_credit + ")"
          }
          onInputChange={(event, newInputValue) => {}}
          onChange={(event, newValue) => {
            // const temp = [...newValue.filter(singleValue => !(singleValue in selectedPlayer))]

            setSelectedPlayer([
              ...selectedPlayer.filter((sp) => sp.role !== "Bowler"),
              ...newValue,
            ]);
          }}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Bowler"
              placeholder="Select Bowler Example :- PlayName(Player Credit)"
            />
          )}
        />

        <table>
          <tbody>
            <style>
              {`table {
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;
}
td, th {
  border: 1px solid #dddddd;
  text-align: center;
  padding: 8px;

}
tr:nth-child(even) {
  background-color: #dddddd;

}`}
            </style>

            <tr>
              <th colSpan="3">Bowler's Table</th>
            </tr>
            <tr>
              <th>Sr No</th>
              <th>Player Name</th>
              <th>Player Credit Score</th>
            </tr>

            {/* <td></td> */}
            {selectedPlayer
              .filter((info) => info?.role == "Bowler")
              .map((item, index) => {
                return (
                  <tr>
                    <td>{index + 1}</td>
                    <td>{item.name}</td>
                    <td>{item.event_player_credit}</td>
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>
    </Stack>
  );
}
